import { useEffect, useMemo, useState } from 'react'
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import React from 'react'
import { useReactTable, getCoreRowModel, flexRender } from '@tanstack/react-table'
import { Button, Checkbox } from '@mui/material';

type TodoData = {
  id : number,
  todo : string,
  completed : boolean,
  usedId : number
}

const TodoTable = () => {
  const [data, setData] = useState<TodoData[]>([])

  // console.log("Local Storage",tdata)
  // console.log("Converted Data",data)

  const api = async() =>{
    const data = await fetch("https://dummyjson.com/todos");
    const jsonData = await data.json();
    console.log("Dataaa",jsonData)
    // const parseData = JSON.parse(String(jsonData.results))
    setData(jsonData.todos)
  }
  useEffect( () => {
    // let tdata = localStorage.getItem("task");
    // const data = JSON.parse(String(tdata));
    api()

  }, [])
  // console.log("JSON",data)

  //   const handaleditclick =(id : string)=>{
  //     console.log('ID---->',id);

  //   }

  //   const handalclick=(index:number)=>{
  // console.log(index)

const columns = [
  {
    header: "Index",
    accessorKey: "id",
    footer: "Name"
  },
  {
    header: "Todo",
    accessorKey: "todo",
    footer: "Task"
  },
  {
    header: "Done",
    accessorKey: "completed",
    cell : ({row : {original : {completed}}}:{
      row : {
        original : {
          completed : boolean
        }
      }
    } ) =>{
       return( <Checkbox checked={completed} inputProps={{ 'aria-label': 'controlled' }} />)
    }
  },
  {
    header: "Edit",
    cell: ({ row: { id } }: 
      { row: { id :string}
     }) => {
      return (
        <Button variant='outlined' >Edit</Button>
      )
    }
  },
  {
    header: "Delete",
    // cell: ({ row: { index } }: 
    //  { row: { index :number}
    // }) => {
    //   console.log('ppppppppppp>',)
    //   return (
    //     <Button variant='outlined' onClick={()=>handalclick(index)}>Delete</Button>
    //   )
    // }
  }

];



const table = useReactTable({
  data,
  columns,
  getCoreRowModel: getCoreRowModel(),
});

return (
  <div style={{ padding: "30px" }}>
    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} size="small" aria-label="a dense table">
        <TableHead>
          {table.getHeaderGroups().map(headerGroup => {
            return (
              <TableRow key={headerGroup.id}>
                {
                  headerGroup.headers.map(header => {
                    return (

                      <TableCell key={header.id}>
                        {flexRender(header.column.columnDef.header, header.getContext())}
                      </TableCell>

                    )
                  })
                }

              </TableRow>

            )
          })}

        </TableHead>
        <TableBody>
          {table.getRowModel().rows.map(row => {
            return (
              <TableRow key={row.id}>
                {row.getVisibleCells().map(cell => {
                  return (
                    <TableCell key={cell.id}>
                      {flexRender(cell.column.columnDef.cell, cell.getContext()
                      )}
                    </TableCell>

                  )
                })}

              </TableRow>

            )
          })}
        </TableBody>
      </Table>
    </TableContainer>
  </div>
)
}

export default TodoTable;
