import { useEffect, useMemo, useState } from 'react'
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import React from 'react'
import { useReactTable, getCoreRowModel, flexRender } from '@tanstack/react-table'
import { Button, ButtonGroup, Checkbox } from '@mui/material';

const TodoTable: React.FC = () => {
  const [data, setData] = useState([])

  // console.log("Local Storage",tdata)
  // console.log("Converted Data",data)
  useEffect(() => {
    let tdata = localStorage.getItem("task");
    const data = JSON.parse(String(tdata));
    setData(data)
  }, [])


  const handalclick=(i:number)=>{
    console.log('i',i)

  }
  const columns = [
    {
      header: "Name",
      accessorKey: "name",
      footer: "Name"
    },
    {
      header: "Task",
      accessorKey: "task",
      footer: "Task"
    },
    {
      header: "Done",
      accessorKey: "isDone",
      cell: ({ row: { original: { isDone } } }: {
        row: {
          original: {
            isDone: boolean;
          };
        };
      }) => {
        return (
          // console.log("ROWSSS", row.row.original.isDone)

          <Checkbox checked={isDone} inputProps={{ 'aria-label': 'controlled' }} />
        )
      }
    },
    {
      header: "Edit",
      cell: ({ row: { original: { isDone } } }: {
        row: {
          original: {
            isDone: any;
          };
        };
      },index:number) => {
        return (
          <Button variant='outlined' onClick={()=>handalclick(index)}>Edit</Button>
        )
      }
    },
    {
      header: "Delete",
      cell: (row: any) => {
        return (
          <Button variant='outlined'>Delete</Button>
        )
      }
    }

  ];



  const table = useReactTable({
    data,
    columns,
    getCoreRowModel: getCoreRowModel(),
  });

  return (
    <div style={{ padding: "30px" }}>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} size="small" aria-label="a dense table">
          <TableHead>
            {table.getHeaderGroups().map(headerGroup => {
              return (
                <TableRow key={headerGroup.id}>
                  {
                    headerGroup.headers.map(header => {
                      return (

                        <TableCell key={header.id}>
                          {flexRender(header.column.columnDef.header, header.getContext())}
                        </TableCell>

                      )
                    })
                  }

                </TableRow>

              )
            })}

          </TableHead>
          <TableBody>
            {table.getRowModel().rows.map(row => {
              return (
                <TableRow key={row.id}>
                  {row.getVisibleCells().map(cell => {
                    return (
                      <TableCell key={cell.id}>
                        {flexRender(cell.column.columnDef.cell, cell.getContext()
                        )}
                      </TableCell>

                    )
                  })}

                </TableRow>

              )
            })}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  )
}

export default TodoTable;
