import { useMemo, useState } from 'react'
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import React from 'react'
import { createTable, useReactTable, getCoreRowModel, flexRender, ColumnDef, } from '@tanstack/react-table'

type ItodoData = {
  name : string,
  task : string,
  isDone : boolean
}
const TodoTable: React.FC = () => {
  let Tdata: any = localStorage.getItem("task") || []
  // const data = JSON.parse(String(tdata));

  const columns = [
    {
      header: "Name",
      accessorKey: "name",
      footer: "Name"
    },
    {
      header: "Task",
      accessorKey: "task",
      footer: "Task"
    },
    {
      header: "Done",
      accessorKey: "isDone",

    },
    {
      header: "Edit",

    },
    {
      header: "Delete"
    }
  ]
  // const todoData: T[] = useMemo(() => tdata, [])

  // console.log([...todoData],'todoData')
  // const dataTable: any=[...todoData]
  // interface ReactTableProps<T extends object> {
  //   data: T[];
  //   columns: ColumnDef<T>[];
  //  }

  //  export const Table = <T extends object>({ data, columns }: ReactTableProps<T>) => {
    const table = useReactTable ({
      Tdata,
      columns,
      getCoreRowModel: getCoreRowModel(),
    });



  return (
    <div>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} size="small" aria-label="a dense table">
          <TableHead>
            {table.getHeaderGroups().map(headerGroup => {
              return (
                <TableRow key={headerGroup.id}>
                  {
                    headerGroup.headers.map(header => {
                      return (

                        <TableCell key={header.id}> {flexRender(header.column.columnDef.header, header.getContext())}</TableCell>

                      )
                    })
                  }
                </TableRow>
              )
            })}
          </TableHead>
          <TableBody>
            {/* {table.getRowModel().rows.map(row => {
              return (
                <TableRow key={row.id}>
                  {row.getVisibleCells().map(cell => {
                    return (
                      <TableCell key={cell.id}>
                        {flexRender(cell.column.columnDef.cell, cell.getContext())}

                      </TableCell>
                    )
                  })}
                </TableRow>
              )
            })} */}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  )
}

export default TodoTable;
