import { useMemo, useState } from 'react'
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import React from 'react'
import { createTable, useReactTable, getCoreRowModel, flexRender, ColumnDef, } from '@tanstack/react-table'
type ItodoData = {
  name: string,
  task: string,
  isDone: boolean
}

const TodoTable: React.FC = () => {
  let data: string | null = localStorage.getItem("task")
  const tdata = JSON.parse(String(data));
  console.log('tdata', tdata)

  const todoData = useMemo(() => tdata, [])
  // const [todoData , setTodoData] = useState<ItodoData>()
  const columns = [
    {
      header: "Name",
      accessorKey: "name",
      footer: "Name"
    },
    {
      header: "Task",
      accessorKey: "task",
      footer: "Task"
    },
    {
      header: "Done",
      accessorKey: "isDone",

    },
    {
      header: "Edit",

    },
    {
      header : "Delete"
    }
  ]
  console.log(tdata)

  const table = useReactTable({
    columns,
    ...todoData

  })
  console.log(table)
  return (
    <div>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} size="small" aria-label="a dense table">
          <TableHead>
            {table.getHeaderGroups().map(headerGroup => {
              return (
                <TableRow key={headerGroup.id}>
                  {
                    headerGroup.headers.map(header => {
                      return (

                        <TableCell key={header.id}> {flexRender(header.column.columnDef.header, header.getContext())}</TableCell>

                      )
                    })
                  }
                </TableRow>
              )
            })}
          </TableHead>
          <TableBody>

          </TableBody>
        </Table>
      </TableContainer>
    </div>
  )
}

export default TodoTable;
